float f_n_sin_interpolated(
    float n_t
){
    float n_tau = 6.2831;
    float a = -1.*cos(n_t*n_tau*.25);
    float b = cos((n_t-1.)*n_tau*.25);
    float c = n_t*b + (1.-n_t)*a;
    return c;
}
float f_n_exp_interpolated(
    float n_t 
){
        float a = n_t*n_t;
        float b = pow(n_t-1.,2.);
        float c = n_t*b + (1.-n_t)*a; 
        return c;

}
//https://www.desmos.com/calculator/khmgdiws0b
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/iResolution.yy;

    float n_its = 9.;
    float n_tau = 6.2831;
    vec4 o_col_min = vec4(0.);
    vec4 o_col = vec4(0.);
    o_trn*=1.5;
    for(float n_it_nor = .0; n_it_nor<1.;n_it_nor+=(1./n_its)){

        float n_ang_nor = fract((atan(o_trn.y, o_trn.x)/n_tau));
        float n_ang_intensity = (n_ang_nor-.5);//(sin(n_ang_nor*2.)*.5+.5);
        float c = f_n_sin_interpolated(sin(n_it_nor*n_tau+n_it_nor+iTime)*.5+.5);
        float n_radius = n_it_nor+.2
            +(sin(n_ang_nor*22.*n_tau))*.2*c;

        float n = length(o_trn*(1./n_radius));
        n = clamp(abs(n-.5)*2., 0., 1.);
        float n_exponent = n_its;
        n = pow(1.-n, n_exponent);

        float nin2 = fract(n_it_nor+iTime*.2);
        o_col += vec4(n)*vec4(
            nin2,
            1.-nin2,
            fract(.5+nin2),
           1.);

    }
    fragColor = o_col;
}
