#define TEXT_ROWS 4

vec4 char(vec2 p, int c) {
    if (p.x < 0. || p.x > 1. || p.y < 0.|| p.y > 1.) return vec4(0,0,0,1);
    return texture(iChannel0, p/16. + fract(vec2(c, 15-c/16)/16.));
}

#define A(c) text = min(text, char(uv * float(7) + vec2(-0.45 * cursor.x, cursor.y) + 0.02 * (hash22(cursor) - 0.5), c).w); cursor.x++;
#define B(c) text = min(text, char(uv * float(TEXT_ROWS) + vec2(-0.45 * cursor.x, cursor.y) + 0.02 * (hash22(cursor) - 0.5), c).w); cursor.x++;
#define CRLF cursor.x = 0.; cursor.y += 0.8;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    float text = 1.;

    vec2 cursor = vec2(0,-5);
    A(0x70) A(0x72) A(0x69) A(0x6E) A(0x74) A(0x66) A(0x28) A(0x22)
    A(0x48) A(0x65) A(0x6c) A(0x6c) A(0x6f) A(0x2c) A(0x77) A(0x6f)
    A(0x72) A(0x6c) A(0x64) A(0x21) A(0x5C) A(0x6E) A(0x22) A(0x29) 
    CRLF
    // Hello world!
    cursor = vec2(0,-1);
    if (iTime > 1.0) B(0x48)
    if (iTime > 1.1) B(0x65)   
    if (iTime > 1.2) B(0x6c)
    if (iTime > 1.3) B(0x6c)
    if (iTime > 1.4) B(0x6f)
    if (iTime > 1.5) B(0x2c)
    if (iTime > 1.6) B(0x77)
    if (iTime > 1.7) B(0x6f)
    if (iTime > 1.8) B(0x72)
    if (iTime > 1.9) B(0x6c)
    if (iTime > 2.0) B(0x64)
    if (iTime > 2.1) B(0x21)

    float ink = smoothstep(0.5, 0.49, text);
    ink -= smoothstep(0.65, 0.75, FBM(25. * uv.xyy));
    fragColor = vec4(0.6, 0.45, 0.8, 1);
    fragColor = mix(fragColor, vec4(0.8, 0.25, 0.7, 1), smoothstep(0.55, 0.49, text));
    fragColor = mix(fragColor, vec4(0), clamp(ink, 0., 1.));
    fragColor += 0.05 * FBM(300. * uv.xyy);
    fragColor = pow(fragColor, vec4(1./2.2));
}

