void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.1415926536;

    float a = sqrt(sin(iTime / 5.0)+ 10.0);
    float b = sin(tan(sign(iTime / 5.0)));
    float n = cos(iTime / 5.0) * 7.0 - 10.0;
    float m = sin(iTime / 5.0) * 7.0 - 10.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float val = a * sin(PI * uv.x * n) * sin(PI * uv.y * m)
    + b * sin(PI * uv.x * m) * sin(PI * uv.y * n);

    vec3 col = vec3(sin(tan(val) / 10.0), cos(tan(val) / .5), sign(val));

    // Output to screen
    fragColor = vec4(col,1.0);
}
