// Emergence 2 By Rik Riesmeijer, 2024 - No Rights reserved.
void mainImage(out vec4 c, in vec2 v) {
    #define r(e) (94587u * e ^ e.wxyz)
    #define tx(e) texelFetch(iChannel0, ivec2(v + e), 0)
    vec2 o = vec2(-1.0 * sin(iTime * 2.1), -1.0 * cos(iTime * 2.3));
    vec4 t = tx(0.);
    vec4 ta = tx(o * sin(iTime)),
         tb = tx(o * cos(iTime * 3.2)), 
         tc = tx(o.xx * cos(iTime)), 
         td = tx(o.xx * sin(iTime * 3.));
    #define f(e) (0.25 * (e.x + e.y + e.z + e.w))
    c = vec4(ta.r, tb.g, tc.b, td.a);
    c = length(t) > length(c)? t - cos(iTime / 5.) * 0.09 * c - 0.0001 * t.r
                             : c + cos(iTime / 3.) * 0.05 * t + 0.001 * t.b;
    c.b = max(c.b, 0.0001);
    c.b -= c.b == 1.? 0.01 : 0.0001;
    c.r += c.b > 0.99? 0.1 : 0.0;
    c.g += c.b > 0.99? 0.2 : 0.0;
    c = iFrame < 1 ? vec4(r(r(r(uvec4(v + v, v))))) / vec4(-1u) + 0.01 : c;
}
