void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float width = iResolution.x / iResolution.y;
    vec2 uv = fragCoord / iResolution.y * 2. - vec2(width, 1.);
    uv *= 3.;

    float equation = sin(uv.x);
    float equation2 = pow(uv.x, 2.);
    float equation3 = -pow(uv.x, 2.);

    equation -= uv.y;
    equation2 -= uv.y;
    equation3 -= uv.y;

    if(mod(uv.y, 1.) <= 0.01 && mod(uv.y, 1.) >= -0.01) {
        fragColor = vec4(0);
    } else if(mod(uv.x, 1.) <= 0.01 && mod(uv.x, 1.) >= -0.01) {
        fragColor = vec4(0.);
    } else if(uv.y <= 0.025 && uv.y >= -0.025) {
        fragColor = vec4(0);
    } else if(uv.x <= 0.025 && uv.x >= -0.025) {
        fragColor = vec4(0.);
    } else if(equation <= 0.02 && equation >= -0.02){
        fragColor = vec4(1., 0., 0., 1.);
    } else if(equation2 <= 0.02 && equation2 >= -0.02){
        fragColor = vec4(0., 1., 0., 1.);
    } else if(equation3 <= 0.02 && equation3 >= -0.02){
        fragColor = vec4(0., 0., 1., 1.);
    } else {
        fragColor = vec4(1.);
    }
}
