mat2 rot(float t) {
    return mat2(cos(t), -sin(t), sin(t), cos(t));
}

float sdChar(vec3 p, float i, float depth) {    

    float ix = floor(mod(i, 16.));
    float iy = floor(i / 16.);   


    vec2 pxy = clamp(p.xy * 0.5 + 0.5, 0., 1.);
    pxy /= 16.0;
    pxy += vec2(ix, 15. - iy) / 16.0;    

    float dxy = texture(iChannel0, pxy).a * 2. - 1.;        
    float dz = abs(p.z) - depth * .5;

    float sig = (dxy < 0. && dz < 0.) ? -1. : 1.;
    dxy = max(dxy, 0.);
    dz = max(dz, 0.);


    float d = sqrt(dxy*dxy + dz*dz) * sig;    
    return d - 0.03;
}

float map(vec3 p) {
    p.xz *= rot(iTime * 0.5);
    p.yz *= rot(sin(iTime * 0.3 + 0.1) * 0.3); 

    p *= .5;


    float d = sdChar(p + vec3(1, 0, 0), 65., 0.5);
    d = min(d, sdChar(p - vec3(.04, 0, 0), 66., 0.5));
    d = min(d, sdChar(p - vec3(1, 0, 0), 67., 0.5));    

    return d * 2.;   
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = vec2(fragCoord / iResolution.xy);
    vec2 p = uv * 2. - 1.;
    p.x *= iResolution.x / iResolution.y;


    vec3 ro = vec3(0, 0, 4.5);
    vec3 rd = normalize(vec3(p, -1));
    vec3 rp;
    float t = 0., d = 0.;    
    float c = 0.;

    for (float i = 0.; i < 120.; i++) {
        rp = ro + rd * t;        
        d = map(rp);

        if (d < 0.003) {        
            c = 1. - i * 0.005 - t * 0.1;
            break;
        }

        t += d * 0.5;
    }


    fragColor = vec4(c);
}
