float spiral(vec2 uv, float size) {
    float r = length(uv) * size;
    float a = atan(uv.y, uv.x);
    float v = a * 2.0 + r * 1.5 + iTime * 2.0;
    return 1.0 - smoothstep(0.7, 0.71, abs(sin(v * 5.0)));
}

mat2 rotate2d(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float rotationSpeed = 1.0 + 0.5 * sin(iTime);
    float angle = radians(rotationSpeed * 30.0);
    vec2 rotatedUV = uv * rotate2d(angle);
    vec4 background = texture(iChannel0, rotatedUV * rotate2d(iTime + sin(iTime * 0.8)));
    float size = 0.8 + 0.4 * sin(iTime);
    vec3 color = 0.5 + 0.5 * cos(iTime + rotatedUV.xyx + vec3(0, 2, 4));
    vec3 col = mix(vec3(0.15), background.xyz, 0.3);
    col = mix(col, color, spiral(rotatedUV, size));
    fragColor = vec4(col, 1.0);
}
