// display char
#define C(c) U.x<.0|| U.x>1. || U.y<0.|| U.y>1. ? O \
               : O += texture( iChannel0, U/16. + fract( vec2(c, 15-(c)/16) / 16. )).x, U.x-=.5

// display decimal + roman digit  // adapted from Envy24 Windows/Angle version.  +22ch
#define D(I,V,X)                                       \
    if (U.y > 1.) U.y--, C(48+t), U.y++; /*decimal */  \
    else if (t>3)                                      \
         if (t<5) C(I),C(V);             /*  IV  */    \
    else if (t<9) C(V);                  /*  V   */    \
    else          C(I), C(X);            /*  IX  */    \
    t%=5; if ( t<4 ) while(t-->0) C(I);  /* I II III */

/**  // --- this version don't seem to work on Windows/Angle
// display decimal + roman digit  
#define D(I,V,X)                                        \
    U.y > 1. ? U.y--, C(48+t), U.y++ :   /*decimal * /  \
      t<4 ? 0.                                          \
    : t<5 ? C(I),C(V)                    /*  IV  * /    \
    : t<9 ? C(V)                         /*  V   * /    \
    :     ( C(I), C(X) );                /*  IX  * /    \
    t %=5; if ( t<4 ) while(t-->0) C(I); /* I II III */
/**/

// 3-digits timer
void mainImage( out vec4 O, vec2 U )
{
    U *= 4./iResolution.y;
    O *= 0.;
    int T = iFrame/10, 
    t = T/100%10;  D(67,68,77);         // C,D,M
    t = T/10 %10;  D(88,76,67);         // X,L,C
    t = T%10;      D(73,86,88);         // I,V,X
}
