vec2 N22(vec2 p){
    vec3 a = fract(p.xyx * vec3(123.34,234.34 ,345.65 ));
    a += dot(a,a+34.35 );
    return fract(vec2(a.x*a.y,a.y*a.z ));

}
// https://www.shadertoy.com/view/ll2GD3
vec3 palette (float t){
    vec3 a = vec3(0.5, 0.5, 0.5); 
    vec3 b = vec3(0.43, 0.43, 0.5); 
    vec3 c = vec3(0.5, 0.9, 0.5); 
    vec3 d = vec3(0.3059, 0.498, 0.6784);

    return .2/(a + b * sin(6.28318*(c*t+d))); 
}

float voronoi(vec2 coord){
    float m = 0.;
    float t = iTime;
    float minDist = 500.;
    for(float i = 0.; i < 100.; i++){
        vec2 n = N22(vec2(i));
        vec2 p = cos(n*t);
        float d = length(coord -p);
        m += smoothstep(.39, .4,d );
        if(d<minDist) {
            minDist =d;
        };
    }
    return minDist;
}

void mainImage(out vec4 fragColor , in vec2 fragCoord){
    vec2 coord = (fragCoord.xy * 2. - iResolution.xy) /iResolution.y;
    vec3 col = vec3(0 );
    vec2 id = floor(coord);

    float vr = voronoi(coord);
    (vr < .03 && vr > .027)? col += smoothstep(.1,1.,palette(iTime*.5)):col+=vec3(vr );
    if (vr >0.02)col +=.1/vec3(0.1922, 0.7686, 0.0745)*vr*2.;

    fragColor =vec4(pow(col,vec3(1.5)),1. );// this almost fried my potato gpu :) don't mess with it

}
