#define R iResolution

float hash(vec2 uv)
{
    return fract(sin(dot(uv,vec2(53.25546,95.64359)))*95657.164138);
}

void mainImage(out vec4 O,in vec2 I)
{
    vec2 u=I/R.xy-.5;
    u.x*=R.x/R.y;
    float r=step(hash(u),.4);
    O=vec4(0);
    float s=sin(length(u)*25.);
    if(abs(s)>.4){if(s>0.)O.r=r;else O.b=r;}
}
