void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float time = iTime;

    vec2 uv = fragCoord / iResolution.xy;

    uv = vec2(uv.x + uv.y, uv.y);

    float offset = time * 0.2; 
    uv.x += offset;

    vec3 col = vec3(step(sin(uv.x * 10.0), 0.5));

    vec3 backgroundColor = vec3(0.0, 1.0, 0.0); 

    fragColor = vec4(backgroundColor * col, 1.0);
}
