#define PI 3.14159265359

vec4 texture_(float idx, vec2 uv) {
    if (uv.x < 0. || uv.y < 0. || uv.x > 1. || uv.y > 1.)
        return vec4(0.);

    if (idx < 1.)
        return texture(iChannel0, uv);
    else if (idx < 2.)
        return texture(iChannel1, uv);
    else if (idx < 3.)
        return texture(iChannel2, uv);
    else if (idx < 4.)
        return texture(iChannel3, uv);
    return texture(iChannel0, uv);
}

const float TEXTURES = 4.;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float t = iTime / 4.;
    float x = t;
    float f = (max(-(floor(x)-(x)+1./2.),0.)+floor(x)/2.)*2.;
    float f2 = abs(sin(f * PI - PI));

    float p = 0.5 + 0.5 * sin(f * PI*2. + PI);
    float q = 0.5 + abs(0.5 * sin(t * PI*2. + PI/2.));
    float times = 1./1.5 + 2.5 * f2;
    float texID = 0.;
    uv -= 0.5;

    uv *= times;

    uv += 0.5;

    uv.x += f * 2.;

    vec2 id = floor(uv);
    vec2 uvt = mod(uv, 1.);
    vec2 uvk = uvt;
    vec2 uvl = uvt;
    uvt -= 0.5;
    uvt *= 1.5;
    uvt += 0.5;

    uvk -= 0.5;
    uvk *= 1.2;
    uvk += 0.5;

    uvl -= 0.5;
    uvl *= 1.3;
    uvl += 0.5;

    float finalTex = mod((id.x + id.y * 2.) / 2. + texID, TEXTURES);


    fragColor = vec4(0.992,0.980,0.647,1.);

    if (mod(id.x + id.y, 2.) == 0.) {
        vec4 shadow = texture_(finalTex, uvk - vec2(0.1, -0.1 * 16./9.));
        if (shadow.a > 0.)
            fragColor.rgb *= 0.8;
        if (uvk.x >= 0. && uvk.y >= 0. && uvk.x <= 1. && uvk.y <= 1.)
            fragColor.xyz = vec3(0.);
        if (uvl.x >= 0. && uvl.y >= 0. && uvl.x <= 1. && uvl.y <= 1.)
            fragColor.xyz = vec3(1.);

        vec4 texel = texture_(finalTex, uvt);
        if (texel.a > 0.)
            fragColor = texel;

    }


}
