void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //colors screen from green to blue
    vec3 background = vec3(0., 1.-uv.x*uv.y, length(uv)+uv.x*uv.y);

    //size and spacing of each square
    float squareSize = 10.;
    float spacing = 100.;

    //calculates position of square and rounds down
    vec2 p = floor(fragCoord / (squareSize + spacing));

    //determines where in the pattern each pixel is and colors if correct
    vec2 isInside = mod(fragCoord, squareSize + spacing) - spacing / 2.0;
    float gridValue = step(0.0, min(isInside.x, isInside.y));

    //adds squares to background
    background += vec3(gridValue);

    // Output to screen
    fragColor = vec4(background,1.0);
}
