mat2 rotate2D(float t) {
  return mat2(cos(t),sin(t),-sin(t),cos(t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
fragColor = vec4(0.0);
float e,i,j,f,a,w;
for(vec3 p,q;i++<50.;p=q+=vec3((fragCoord.xy-.5*iResolution.xy)/iResolution.y,-.5)*e){
  p.yz*=rotate2D(.7);
  f=.4;
  i<45.?p:p-=.001;
  e=p.y+7.;

  for(a=j=.9;j++<30.;a*=.8){
    vec2 m=iResolution.xy/iResolution.xy*rotate2D(j);
    float x=dot(p.xz,m)*f+iTime+iTime;
    w=exp(sin(x)-1.);
    p.xz-=m*w*cos(x)*a;
    e-=w*a;
    f*=1.2;
    }
  }
fragColor-=e*2e3;
}
