float Sphere1(vec3 p)
{
    return Sphere(p, vec3(-5.0, 0.0, 0.0), 3.5);
}

float Sphere2(vec3 p)
{
    return Sphere(p, vec3(-1.5, -6.0, 0.0), 1.5);
}

float Sphere3(vec3 p)
{
    return Sphere(p, vec3(-5.0, 0.0, 0.0), 2.5);
}

float Sphere4(vec3 p)
{
    return Sphere(p, vec3(5.5, 0.0, 0.0), 3.5);
}

float object(vec3 p)
{
    float v = Sphere1(p);

    v = Union(v, Sphere2(p));
    v = Union(v, Sphere3(p));
    v = Union(v, Sphere4(p));

    v = Union(v, Sphere(p, vec3(0.0, 4.0, 0.0), 1.5));

    v = Difference(v, Plane(p, vec3(0.0, -2.0, 0.0), vec3(0.0, 1.0, 0.0)));

    return v;
}

// Fonction Shadow:
// Cette fonction détermine si un fragment est dans l'ombre ou éclairé par une lumière ponctuelle en utilisant
// la technique de Shadow Mapping.
// - fragPos : Position du fragment dans l'espace.
// - lightPos : Position de la lumière ponctuelle.
// - bias : Un petit décalage pour éviter les problèmes de "self-shadowing".
// Retourne 0.0 si le fragment est dans l'ombre, 1.0 s'il est éclairé.
float Shadow(vec3 fragPos, vec3 lightPos, float bias)
{
    // Direction de la lumière
    vec3 lightDir = normalize(lightPos - fragPos);

    // Coordonnées de projection du fragment
    vec3 projCoords = fragPos + lightDir * bias;

    // Conversion des coordonnées de projection en coordonnées de texture
    vec2 texCoords = projCoords.xy / projCoords.z * 0.5 + 0.5;

    // Échantillonnage de la profondeur de la carte d'ombre
    float shadow = texture(iChannel1, texCoords.xy).r;

    // Si la profondeur échantillonnée est plus grande que la distance de la lumière
    if (shadow < projCoords.z - bias)
    {
        return 0.0; // Point dans l'ombre
    }

    return 1.0; // Point éclairé
}




// Analysis of the scalar field

const int Steps=200;// Number of steps
const float Epsilon=.01;// Marching epsilon

// Object normal
// p : point
vec3 ObjectNormal(vec3 p)
{
  const float eps=.001;
  vec3 n;
  float v=object(p);
  n.x=object(vec3(p.x+eps,p.y,p.z))-v;
  n.y=object(vec3(p.x,p.y+eps,p.z))-v;
  n.z=object(vec3(p.x,p.y,p.z+eps))-v;
  return normalize(n);
}

// Trace ray using ray marching
// ray : Ray 
// e : Maximum distance
// h : hit
// s : Number of steps
float SphereTrace(Ray ray,float e,out bool h,out int s)
{
  h=false;

  // Start at the origin
  float t=0.;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=Point(ray,t);
    float v=object(p);
    // Hit object
    if(v<0.)
    {
      h=true;
      break;
    }
    // Move along ray
    t+=max(Epsilon,v);
    // Escape marched too far away
    if(t>e)
    {
      break;
    }
  }
  return t;
}

// Lighting

// Background color
// d : Ray direction
vec3 Background(vec3 d)
{
  return mix(vec3(.45,.55,.99),vec3(.65,.69,.99),d.z*.5+.5);
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p,vec3 n,vec3 e)
{
  // Point light
  const vec3 lp=vec3(5.,10.,30.);

  // Light direction to point light
  vec3 l=normalize(lp-p);

  // Ambient color
  vec3 ambient=.25+.25*Background(n);

  // Shadow computation
  float shadow=1.0;
  // Phong diffuse
  vec3 diffuse=.35*clamp(dot(n,l),0.,1.)*vec3(6.,6.,6.);

  // Specular
  vec3 r=reflect(e,n);
  vec3 specular=.15*pow(clamp(dot(r,l),0.,1.),35.)*vec3(1.,1.,1.);
  vec3 c=ambient+shadow*(diffuse+specular);
  return c;
}

// Image
void mainImage(out vec4 color,in vec2 pxy)  
{
  // Pixel
  vec2 pixel=(-iResolution.xy+2.*pxy)/iResolution.y;

  // Mouse
  vec2 m=iMouse.xy/iResolution.xy;

  // Camera
  Ray ray=CreateRay(m,pixel);


  // Hit and number of steps
  bool hit;
  int s;

   // Trace ray
 float t=SphereTrace(ray,75.,hit,s);

  // Shade background
  vec3 rgb=Background(ray.rd);

  if(hit)
  {
    // Position
    vec3 p=Point(ray,t);

    // Compute normal
    vec3 n=ObjectNormal(p);

    // Shade object with light
    rgb=Shade(p,n,ray.rd);
  }

  color=vec4(rgb,1.);
}
