mat2 rot(float t)
{
    return mat2(cos(t), -sin(t), sin(t), cos(t));
}

float hash21(vec2 p) {
    vec3 v = fract(p.xyx*vec3(67.431, 32.237, 90.012));
    v += dot(v,v+33.33);
    return fract((v.x+v.y)*v.z);
}

float valNoi(vec2 p) {
    vec2 i = floor(p);
    vec2 g = fract(p);
    g= g*g*(3.0-2.0*g);

    return mix(
        mix(hash21(i+vec2(0.0,0.0)),hash21(i+vec2(1.0,0.0)),g.x),
        mix(hash21(i+vec2(0.0,1.0)),hash21(i+vec2(1.0,1.0)),g.x),
        g.y
    );
}

float fbm(vec2 p, int oct) {
    float amp=0.5, f=10.0, v=.0;
    //int oct = 10;

    for (int i=0;i<oct;i++) {
        v+= amp*valNoi(p*f+float(i));
        p*=rot(1.0);
        f*=2.0;
        amp*=0.5;
    }
    return v;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    uv.x+= fbm(uv,7)+3.0;
    float val = fbm(uv, 10);

    col+= val;
    fragColor = vec4(col,1.0);
}
