void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Background color - change over time
    vec3 backgroundColor = 0.5 + 0.5*cos(iTime*0.5 + fragCoord.xyx + vec3(0,2,4));

    // Moving squares colors - change over time
    vec3 squareColor1 = 0.5 + 0.5*cos(iTime*0.5 + fragCoord.xyx + vec3(1,3,5));
    vec3 squareColor2 = 0.5 + 0.5*sin(iTime*0.5 + fragCoord.xyx + vec3(2,4,6));

    // Static square
    vec2 p = fragCoord/iResolution.xy * 2.0 - 1.0;    
    float staticSquare = step(abs(p.x), 0.5) * step(abs(p.y), 0.5);

    // Moving squares position - moving in ellipse around static square
    vec2 center = vec2(0.0, 0.0);
    float a = 0.5;
    float b = 0.3;
    float angle1 = iTime;
    float angle2 = iTime * 1.5;
    vec2 pos1 = center + vec2(a * cos(angle1), b * sin(angle1));
    vec2 pos2 = center + vec2(a * cos(angle2), b * sin(angle2));

    // Draw background
    fragColor = vec4(backgroundColor, 1.0);

    // Draw static square
    fragColor += vec4(staticSquare * squareColor1, 1.0);

    // Draw moving squares
    fragColor += vec4(step(length(fragCoord - pos1), 0.1) * squareColor2, 1.0);
    fragColor += vec4(step(length(fragCoord - pos2), 0.1) * squareColor2, 1.0);

    fragColor.a = 1.0;
}
