void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 o_col_webcam_last = texture(iChannel0, uv);
    vec4 o_col_webcam_now = texture(iChannel1, uv);
    vec4 o_col = o_col_webcam_last-o_col_webcam_now;

    fragColor = abs(o_col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float n_target_fps = 3.+floor(uv.x*20.);
    float n_mod_frame = ceil(ceil(iFrameRate) / n_target_fps);
    //n_mod_frame = floor(uv.x*10.);
    n_mod_frame = 10.;
    if(mod(float(iFrame), n_mod_frame)==0.){
        fragColor = texture(iChannel1, uv);

    }else{
        fragColor = texture(iChannel0, uv);
    }


}
