void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0,uv).rgb / float(iFrame+1);

    fragColor = vec4(col,1.0);
}

//stole this rand function from https://www.shadertoy.com/view/msSXRw
float rand(vec2 co, float seed)
{
    vec4 s = vec4(seed, seed, seed, seed);
    seed = fract(sin(dot(s.xyzw, vec4(123456789.0, 362436069.0, 521288629.0, 88675123.0))) * 43758.5453);
    return fract(sin(dot(co.xy + s.xz ,vec2(12.9898,78.233))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(vec3(rand(uv,float(iFrame)))+vec3(texture(iChannel0,uv)),1.);
}
