#define C vec2(0.5,0.5)
#define R 0.3
#define pi 3.141592
#define half_pi (pi/2.0)
#define r_scale vec2(0.2,0.4)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ar=float(iResolution.x/iResolution.y);

    uv.x = uv.x*ar;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 c=C + 0.5*cos(iTime*3.0)*sin(iTime/5.0-half_pi);
    float r=r_scale.x+r_scale.y*(0.5+0.5*sin(iTime));
    //float r=R/2.;
    float k=cos(pi);
    vec3 col;
    float d=length(c-uv);
    if(abs(d-r)<0.01)
      col=vec3(1.,1.,1.);
    else
      col=vec3(0.,0.,0.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
