void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.xy;


    float val = sin((uv.x + (sin(uv.y * 20.0 + iTime) * 0.05)) * (50.0 + uv.y * 100.0));

    val = pow(val,mix(1.0,50.0, uv.y));


    fragColor = vec4(sin(iTime + 24.0 + uv.y * 35.0) * 0.5 + 0.5,
                     cos(iTime - uv.y * 17.0) * 0.5 + 0.5,
                     sin(iTime + uv.y * 20.0)  * 0.5 + 0.5,0) * val;

}
