//For the Bands to become Blur between 2 points
float BlurBand(float t, float start, float end, float blur)
{
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);

    return step2*step1;
}

//To create the shape
float Rect(vec2 uv, float left, float right, 
                    float bot, float top, float blur)
{
    float band1 = BlurBand(uv.x, left, right, blur);
    float band2 = BlurBand(uv.y, bot, top, blur);

    return band2*band1;
}

//Remap Normalize
float remap01(float a, float b, float t)
{
  return (t-a) / (b-a);  
}

//Remap value a,b to c,d where t is the input*mask for example*
float remap(float a, float b, float c, float d, float t)
{
    return remap01(a,b,t) * (d-c) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float t = iTime;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

    float mask = 0.;

    float x = uv.x;

    float w = (sin(x*8.-t))*.1;

    float y = uv.y-w;

    float blur = remap(-1., 1., .001, .5, x);

    mask = Rect(vec2(x,y), -1., 1., -.05, .05, blur*.6);
    mask += Rect(vec2(x,y), -1., 1., -.09, .09, blur*2.);

    //basically mix is lerp, using main color > into what color u want > the mask
    col = mix(col.rgb, vec3(1.,1.,1.), mask);

    fragColor = vec4(vec3(col), 1);
}
