float Circle(vec2 uv, vec2 p, float r, float blur){

    float d = length(uv-p);
    float c = smoothstep(r, r-blur,d);
    return c;
}

float Band(float t, float start, float end, float blur){
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1*step2;
}

float Smiley(vec2 uv, vec2 pos, float size){
    uv *=size;
    uv -=pos; //translate coordinate system


    float mask = Circle(uv, vec2(0.), 0.4, 0.05);

    mask -= Circle(uv, vec2(.15,0.05),.1,.01);
    mask -= Circle(uv, vec2(-.15,0.05),.1,.01);

    float mouth=Circle(uv, vec2(0.,-.1), 0.15, 0.001);
    mouth-=Circle(uv, vec2(0., -.08), 0.15, 0.001);

    mask -=clamp(mouth, 0., 1.);   
    return mask;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur){
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    return band1*band2;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv -=.5;
    uv.x *= iResolution.x/ iResolution.y;
    vec3 color = vec3(0.);

    float mask = .0;
    mask = Rect(uv, -.2, .2, -.3, .3, .01);
    color = vec3(1.0, 1., 1.)*mask;

    fragColor = vec4(color,1.0);
}
