mat3 rotation(float a) {
    return mat3(
        cos(a), sin(a), 0.,
        -sin(a), cos(a), 0.,
        0., 0., 1.
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    vec2 r2 = (rotation(iTime) * vec3(r,1.0)).xy;

    int itMax = 50;
    int n = 0;
    vec2 z = vec2(0.0,0.0);
    while(n<itMax && sqrt(z.x*z.x+z.y*z.y) < 2.0){
        z = vec2((pow(z.x,2.0)-pow(z.y,2.0) + r2.x),(2.0*z.x*z.y + r2.y));
        n = n+1;
    }

    if (n == itMax) {
        vec3 col = texture(iChannel0,r).xyz;
        fragColor = vec4(col,1.0);
    }
    else {

        vec3 col = texture(iChannel1,r).xyz;
        fragColor = vec4(col,1.0);
    }
}
