float fns(float n, float nt){
    return (sin(n+nt)*.5+.5);
}
float f_n(
    vec2 o_trn, 
    float n_its, 
    float n_t,
    float n_scl_min
){

    float n_tau = radians(360.);
    float n_min = 1.;
    float n_col = 1.;
    float n_dcntr = length(o_trn);
    n_t+=n_dcntr;
    for(float n_it = 0.; n_it < n_its; n_it+=1.){

        float n_it_nor = n_it / n_its;
        float noff = 0.;
        float n_amp = 0.5;//(sin(iTime)*.5+.5)*.2;
        vec2 o = vec2(
            sin((n_it_nor)*n_tau), 
            cos((n_it_nor)*n_tau)
        )*n_amp-o_trn;

        float n = max(abs(o.x), abs(o.y));
        float nc = length(o);
        float t1 = fns(0., n_t+n_dcntr*18.);
        n = t1 * nc + (1.-t1)*n;
        //n = sin(n*33.*(1./nc))*.5+.5;
        //n = sin(n*22.*(1./nc))*.5+.5;

        n = sin(n*52.*pow(n, fns(0.,n_t)*.2)+n_t)*.5+.5;
        n = pow(n, 1./(fns(n_dcntr,n_t)*20.));

        float n_res = n;


        n_col*=n_res;
    }

    n_col = clamp(n_col, 0., 1.);


    return n_col; 
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float n_scl_min = min(iResolution.x, iResolution.y);
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/n_scl_min;
    float nt = iTime*2.;
    vec3 o_col = vec3(
        1.-f_n(o_trn, 4., nt+0.1, n_scl_min),
        1.-f_n(o_trn, 4., nt+0.2, n_scl_min),
        1.-f_n(o_trn, 4., nt+0.3, n_scl_min)
    );
    fragColor = vec4(o_col, 1.);
}
