vec3 circle(vec2 uv, float yPos, float size, float feather, vec3 color) {
    vec2 centerUV = (uv*2.0-1.0);
    centerUV.x *= (iResolution.x/iResolution.y);
    centerUV.y += yPos;
    float center = length(centerUV);
    center = smoothstep(size, size-feather, center);
    vec3 colorResult = color*center;

    return colorResult;
}

vec3 makeLight(vec2 uv) {
    float interval = (cos(iTime/1.0)+1.0)/2.0;

    vec3 circle1 = circle(uv, -0.65, 0.25, 0.05, vec3(0.0, 0.5, 0.0));
    vec3 circle2 = circle(uv, -0.0, 0.25, 0.05, vec3(0.5, 0.5, 0.0));
    vec3 circle3 = circle(uv, 0.65, 0.25, 0.05, vec3(0.5, 0.0, 0.0));

    if (interval >= 0.0 && interval <= 0.45) {
        circle1 = circle(uv, -0.65, 0.35, 0.2, vec3(0.0, 1.0, 0.0));
    }

    if (interval >= 0.45 && interval <= 0.57) {
        circle2 = circle(uv, -0.0, 0.35, 0.2, vec3(1.0, 1.0, 0.0));
    }

    if (interval >= 0.57 && interval <= 1.0) {
        circle3 = circle(uv, 0.65, 0.35, 0.2, vec3(1.0, 0.0, 0.0));
    }

    vec3 result = circle1+circle2+circle3;

    return result;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(makeLight(uv), 1.0);
}
