#define S(v) smoothstep( 5./R.y, -5./R.y, v )  
#define H(p)  (fract(sin( 17.+ float(p)* vec2(12.9898, 78.233)) * 43758.5453)*2.-1.)*R/R.y

float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.) );
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;

    O *= 0.;
    for( int i; i++ < 60 ; )
        O += S( line( U, H(i),H(-i) )          // add optical depth of paint layers
                - .1 +.03*texture(iChannel0,U).r );  // fuzzy stroke

    O += .5*texelFetch(iChannel0,ivec2(u)%256,0 ).r; // paper grain

    if ( U.y < -.9 ) O = vec4(10.*u.x/R.x);    // bottom: full gradient of paint thickness

    O = exp( - .15* O / vec4(1,.7,.5,0) );     // convert to transmittance
}
