// Fork of " Light" by wyatt. https://shadertoy.com/view/McByWR
// 2024-07-28 00:30:33

Main {
    Q = A(U)/float(iFrame);
    if (U.y>iMouse.y)
        Q = vec4(length(Q.xy));
    else {
        float f = 10.;
        if (length(2.*(U-.5*R)/R.y)<.4) f = 5.;
        Q.xy *= ei(f*iTime);

        Q = vec4(Q.x,Q.y,-Q.x,1);
    }
}

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.14159265359
vec2 quadratic(float a, float b, float c) {
    float det = b*b-4.*a*c;
    if (det < 0.) return vec2(-1);
    return vec2(-b-sqrt(det),-b+sqrt(det))/2./a;
}

vec2 sphere(vec3 p, vec3 d,float r) {

    // (p + d*x)^2 = r^2
    // pp-rr + 2pdx + ddxx = 0

    return quadratic(1.,2.*dot(p,d),dot(p,p)-r*r);
}

vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main {
    Q = vec4(0);
    for (float k = 0.; k < 10.; k++) {
        vec4 h = hash44(vec4(U,k,iFrame));
        float a = 2.*pi*h.x;
        vec3 d = vec3(cos(a),sin(a),0);
        vec3 p = vec3(2.*(U-.5*R)/R.y,0);
        float pl = 0.;
        for (float j = 0.; j < 12.; j++) {
            float r = .4;
            vec2 i = sphere(p,d,r);
            vec3 c = vec3(0);
            float s = sign(length(p-c)-r);
            float dl = 1.5;//(2.+1.*h.w);
            if (s>0.)dl=1.;
            if (i.x >0. &&i.x<i.y) {
                p += d*i.x;
                pl+= dl*i.x;
            } else if (i.y>0.){
                pl+= dl*i.y;
                p += d*i.y;
            } else {
                pl+= dl*(10.-p.x-cos(d.y));
                break;
            }
            vec4 z = hash44(vec4(U,k*8.+j,iFrame));
            vec3 no = normalize(p-c);
            no *= -sign(dot(p-c,d));
            float q = 1.5;
            if (z.x<.5) d = reflect(d,no);
            else d = refract(d,no,1./q);
            p += d*1e-4;
        }
        float angle = atan(d.y,d.x);
        float e = exp(-1.*abs(angle));
        float theta = pl*R.x/2./pi/1.5;
        Q.xy += vec2(cos(mod(theta,2.*pi)),sin(mod(theta,2.*pi)))*e*1.5;
    }
    Q += A(U);
}
