void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Verbesserte Audio-Analyse
    float bassFreq = texture(iChannel0, vec2(0.1, 0.1)).r;
    float midFreq = texture(iChannel0, vec2(0.5, 0.1)).r;
    float highFreq = texture(iChannel0, vec2(0.9, 0.1)).r;
    float totalVolume = (bassFreq + midFreq + highFreq) / 3.0;

    // Dynamische Zeitsteuerung basierend auf der Musik
    float timeScale = 0.75 + totalVolume * 2.0;
    float time = iTime * timeScale;

    // Richtungsänderung basierend auf Frequenzen
    vec2 direction = vec2(
        cos(time * 0.5 + bassFreq * 10.0),
        sin(time * 0.7 + highFreq * 8.0)
    );
    uv += direction * 0.1 * totalVolume;

    vec2 mouse = iMouse.xy / iResolution.xy;

    // Octopus-Körper und Arme mit dynamischer Anpassung
    float angle = atan(uv.y, uv.x);
    float radius = length(uv);
    float armCount = 8.0 + sin(time) * 2.0 + midFreq * 10.0; // Armanzahl reagiert auf Mittenfrequenz
    float armSpeed = 7.0 + highFreq * 20.0; // Armgeschwindigkeit reagiert auf Hochfrequenz
    float armPattern = sin(armCount * angle + time * armSpeed - radius * 20.0 + mouse.x * 10.0);

    // Verbesserte Neon-Dots für die Tentakel
    float dotFrequency = 80.0 + bassFreq * 100.0; // Dot-Frequenz reagiert auf Bass
    float dotPattern = smoothstep(0.01, 0.02, cos(radius * dotFrequency + armPattern * 8.0 + time * 2.0));

    // Komplexe Farbberechnung mit Audio-Reaktivität
    vec3 color = vec3(
        0.5 + 0.5 * sin(armPattern * 10.0 + time + bassFreq * 15.0),
        0.5 + 0.5 * sin(armPattern * 8.0 + time + midFreq * 12.0),
        0.5 + 0.5 * sin(armPattern * 12.0 + time + highFreq * 18.0)
    );

    // Zusätzliche dynamische Farbeffekte
    color *= 1.0 + sin(radius * (20.0 + totalVolume * 30.0) - time * 5.0) * 0.2; // Dynamischer Ringeffekt
    color += vec3(0.1, 0.05, 0.2) * smoothstep(0.4, 0.6, sin(angle * (16.0 + midFreq * 20.0) + time * 2.0)); // Dynamische Streifen

    // Verbesserte Glüheffekte
    float glow = smoothstep(0.1, 0.25, armPattern);
    color *= glow * (1.5 + totalVolume);
    color += vec3(0.1, 0.1, 0.3) * pow(glow, 3.0 + highFreq * 5.0); // Dynamisches blaues Glühen

    // Dynamischer Hintergrundeffekt
    vec3 bgColor = vec3(0.05, 0.05, 0.1) + 0.05 * sin(uv.xyx * (10.0 + bassFreq * 20.0) + time);
    color = mix(bgColor, color, smoothstep(0.2, 0.21, armPattern));

    // Anwenden des verbesserten Dot-Musters
    color *= 1.0 + dotPattern * vec3(0.5 + bassFreq, 0.7 + midFreq, 1.0 + highFreq);

    // Dynamischer Vignette-Effekt
    float vignetteStrength = 0.5 + totalVolume * 0.5;
    float vignette = smoothstep(1.0, vignetteStrength, length(uv));
    color *= vignette;

    // Zusätzlicher Pulseffekt basierend auf dem Beat
    float beatPulse = sin(time * 10.0) * 0.5 + 0.5;
    color *= 1.0 + beatPulse * totalVolume * 0.3;

    // Finale Farbausgabe mit verstärktem Pulsieren
    fragColor = vec4(color * (1.0 + totalVolume * 0.4), 1.0);
}
