#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define PI acos(-1.)
#define t iTime

vec3 cor, g;

float gyr(vec3 p, float e) {
    g = p;
    return dot(sin(p), cos(p.zxy)) + e;
}

float piece(vec3 p, float e, float a, float b, float c, float d) {
    vec3 q = p * 10.;
    q.xz *= rot(.3 * t + c);
    q.yz *= rot(.4 * t + d);
    q.xz += a;
    return max((length(p) - b) * 1.5, .65 * gyr(q, e));
}


float map(vec3 p) {
    p.xy *= rot(.2 * dot(vec2(cos(t * 2.), sin(t * 3.)), p.yx));    

    float s = smoothstep(.0, .2, cos(t) + .75),
          white = piece(p, 1.2, 0., 1.3 + s * .5, 0., 0.), 
          red = piece(p, .8, PI, 1.4, .02 * cos(11. * t), .03 * sin(13. * t)), 
          d = min(white, red);

    cor = d == red
        ? mix(vec3(1, 0, 0), vec3(1), 
              smoothstep(.4, .0, 
                  cos((dot(sin(g), cos(g.zxy))) * 32.) * .5 + .5))
        : vec3(1, vec2(step(white, red)));

    return min(white, red) * .09;
}

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
         u = (u - r.xy / 2.) / r.y;

    vec3 p = vec3(0, 0, - 3),
         D = vec3(u, 1);

    float i, s, d,
          far = 6.;

    while(i ++ < 150. && d < far)
        p += s * D,
        d += s,
        s = map(p);

    o = vec4(-u.y * .2);
    if(d < far) 
        o = vec4(7. / exp(d * 1.8)),
        o.rgb *= cor;
}




