const float PI   = 3.1415926535;
const float PI_2 = 1.5707963267;

const float timeTravel = 1.5;
const float yMulti = 8.88;
const vec2 fadeBoundaries = vec2(0.035, 1.9);
const float minimumColor = 0.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float x = uv.x * PI * 3.0;
    float y = (uv.y-0.5)*yMulti;
    float time = iTime;
    time *= timeTravel;
    x += time * timeTravel;
    float sineX = sin(x);
    float fadingWave = smoothstep(fadeBoundaries.x, fadeBoundaries.y, abs(sineX-y));
    float c = fadingWave;
    c = 0.01 / c;
    //c = 0.99 - c;
    c += 0.000001;
    float rgbX = x + (time) * abs(cos(0.03*time)); 
    vec3 rgb = vec3(sin(rgbX*0.2+time)+cos(rgbX*0.4), clamp(cos(rgbX*0.5), 0.0, 0.7), smoothstep(0.2, 1.0, sin(rgbX)+cos(rgbX+time*0.5)));
    bool isblack = true;
    for (int i = 0; i < 3; i+=1)
    { 
        int j = i;
        if (rgb[j] > minimumColor)
        { 
            isblack = false;
        }
    }
    if (isblack) 
    { 
        float random=abs(0.5*sin(rgbX))+abs(0.5*sin(rgbX-PI_2));
        rgb[0] = minimumColor*random;
        rgb[2] = minimumColor*(1.0-random);
    }
    float alpha = 0.88;
    rgb = pow(rgb, vec3(2.0));
    vec4 color = vec4(c*rgb.r, c*rgb.g, c*rgb.b, alpha);
    color = 1.0-exp(0.001-color);
    fragColor = color;
}
