vec3 palette(float t) {
    vec3 a = vec3(1.000,1.000,1.000);
    vec3 b = vec3(0.439,0.463,0.435);
    vec3 c = vec3(0.529,0.510,0.525);
    vec3 d = vec3(0.573,0.675,0.808);

    return a + b*sin(.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; 
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.0);

    for(float i = 0.0; i <4.0; i++){

        uv = fract(uv * .5) -0.5;
        float d = length(uv)*900.;
        vec3 col = palette(iTime/1.0 + length(uv0));
        d = sin(d*4.5 + (iTime)/4.0)/8.;
        d = abs(d);
        d = .004 / d;
        finalColor += col * d;

    }

    // Add vignette effect
    vec2 vignetteUV = fragCoord / iResolution.xy;
    vignetteUV = (vignetteUV - 0.5) * 2.0;
    float vignette = 1.0 - dot(vignetteUV, vignetteUV);
    vignette = pow(vignette, 60.5);

    finalColor *= vignette;

    fragColor = vec4(finalColor, 1.0);
}
