// CAUTION: EPILEPSY WARNING

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // pass this in as a uniform
    float uFlashAmount = sin(iTime*30.)*0.5+0.5;

    vec2 uv = fragCoord/iResolution.xy;

    // background for demo purposes
    vec4 bg = texture(iChannel1, uv);
    vec4 color = texture(iChannel0, uv);
    float alpha = color.a;
    vec4 white = vec4(1.0*alpha, 1.0*alpha, 1.0*alpha, 0.0);
    color = mix(bg, color, alpha);
    fragColor = mix( color,white, uFlashAmount*alpha);
}
