void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates 
    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    float object = length(uv - vec2(0.5)) - 0.25;

    // Lighting calculations
    vec3 lightDir = normalize(vec3(0.0, 0.0, -1.0)); // Directional light
    vec3 pointLightPos = vec3(0.5, 0.5, -0.5); // Point light position
    vec3 spotLightDir = normalize(vec3(0.0, -1.0, -1.0)); // Spot light direction
    float spotEffect = dot(spotLightDir, normalize(pointLightPos - vec3(uv, 0.0)));

    // Apply lighting to the object
    col *= smoothstep(0.1, 0.3, object); // Object color affected by light
    col += vec3(1.0, 0.5, 0.0) * max(dot(lightDir, vec3(0.0, 0.0, 1.0)), 0.0); // Directional light effect
    col += vec3(0.0, 1.0, 0.0) * pow(max(spotEffect, 0.0), 10.0); // Spot light effect
    col += vec3(1.0, 0.0, 0.0) * length(pointLightPos - vec3(uv, 0.0)); // Point light effect

    // Output to screen
    fragColor = vec4(col,1.0);
}
