float sd(vec2 p, vec2 f)
{
    float d = cos(length(p))+cos(length(f));
    for (float i=0.; i<=1.; i++) {
        d = (sin(16.0*d + iTime)/16.0);
    }
    d = abs(d);
    return d;
}
vec3 color(float p) {
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.00,0.33,0.67);
    return a + b * cos(2.0*3.14*(c*p+d));
}

mat2 rotate(float angle) {
    float cosAngle = cos(angle);
    float sinAngle = sin(angle);
    return mat2(cosAngle, -sinAngle, sinAngle, cosAngle);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;
    vec2 uv0 = uv;
    uv = fract(uv*5.0)-0.5;

    vec3 pallete = color((length(uv0)+length(uv))/2.0-0.1*iTime);

    mat2 rotation = rotate(iTime);
    //uv = uv * rotation;

    float d = sd(uv, uv0);
    d = 0.02/d;
    vec3 final_color = pallete*d;
    fragColor = vec4(final_color, 1.0);
}
