#define rot mat2(tanh(cos(iTime * .3 + vec4(0, 11, 33, 0)) * 5.))
#define pi acos(-1.)

float sdPentagon(vec2 p, float r, vec2 id) {
    vec2 e = vec2(-1, 1),
         k = cos(pi / 5. + vec2(0, 11));

    float tn = r * k.y / k.x;

    //flip
    if(mod(id.x, 2.) == 1.)
        p.y = -p.y;

    p.x = abs(p.x);

    p -= 2. * min(dot(k * e, p), .0) * k * e;
    p -= 2. * min(dot(k, p), .0) * k;
    p -= vec2(clamp(p.x, -tn, tn), r);

    return length(p) * sign(p.y);
}

float ra = .29;
void hexgrid(vec2 u, out vec2 p, out vec2 id) {
    vec2 grid = vec2(1., .7);
    vec2 a = mod(u, grid) - grid / 2.,
         b = mod(u + grid / 2., grid) - grid / 2.;

    p = a;
    id = floor(u / grid) * 2.;
    vec2 id2 = floor(u / grid - .5) * 2. - 1.;

    if(
        sdPentagon(a, ra, id) 
        > sdPentagon(b, ra, id2)

      ) {

        id = id2;
        p = b;
    }
}

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy, p, id;
    u = (u + u - r) / r.y * rot 
            * (.8 + .3 * tanh(cos(iTime * .7) * 6.)) 
        + iTime * .4;

    hexgrid(u, p, id);

    float x = mod(mod(id.x, 2.) * 2. - 1. + id.y, 3.);

    o = smoothstep(.01, .0, sdPentagon(p, ra, id) + .01) 
             * (vec4(x <= 1., x < 1., x <= 2., 0))
             * (.3 - .5 * length(rot * p + .05 + cos(length(p) * 120.) * .01));

    o = pow(o, vec4(.25)) + .01;
}
