float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    float d =sdCircle(p,0.7);
    float AA=3.0/min(iResolution.x, iResolution.y);

    float wave = sin(d * 20.0 - iTime * 5.0 + atan(p.y, p.x));
    vec3 col=vec3(0.0, 0.3 * cos(iTime + wave),0.5 + 0.5 * cos(iTime + wave));

    col = mix( col, vec3(0.5), smoothstep(AA,0.8,abs(d)));
    fragColor = vec4(col, 1.0);
}
