// Fork of "Nothing New" by wyatt. https://shadertoy.com/view/Xf33zX
// 2024-04-06 01:33:32

Main {
    Q = .8*atan(2.*D(U)/float(iFrame - M));
    if (iFrame < M) Q = A(U).wwww;
 }

#define M 5000

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)

#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)

#define vel(v) (v)*inversesqrt(1.+dot(v,v))

#define inside(v) ((v).x>0.&&(v).y>0.&&(v).x<R.x&&(v).y<R.y)

float ln (vec3 p, vec3 a, vec3 b) { 
    return length(p-a-(b-a)*(dot(p-a,b-a)/dot(b-a,b-a)));
}
 #define UIF (1.0 / float(0xffffffffU))
uint baseHash(uvec2 p){
    p = 1103515245U*((p >> 1U)^(p.yx));
    uint h32 = 1103515245U*((p.x)^(p.y>>3U));
    return h32^(h32 >> 16);
}
vec3 hash(vec2 x){
    uint n = baseHash(floatBitsToUint(x));
    uvec3 rz = uvec3(n, n*16807U, n*48271U);
    return vec3((rz >> 1) & uvec3(0x7fffffffU))/float(0x7fffffff);
}
#define pi 3.141592659

Main {

    Q = A(U);
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    if (x*x+y*y<=4)
    if (x!=0||y!=0) {
        vec2 v = vec2(x,y);
        vec4 q = A(U+v);
        if (inside(U+v))
        Q.xy += .01*q.w*(1.-q.w)*v/dot(v,v);
    }
    Q.y -= .1/R.y;
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy *= 0.;
    if (iFrame < 1) {
        Q.z = U.x/R.x;
        Q.w = step(U.y,.25*R.x);
        Q.w -= step(abs(U.x-.5*R.x),.05*R.x);
        Q.w = max(Q.w,0.);
    }
    if (iMouse.z>0.&&length(U-iMouse.xy)< 10.) Q.w = 1.,Q.x = .5,Q.z=1.;

    if (iFrame > M) Q = B(U);
}

Main {

Q = A(U);
vec4 dQ = vec4(0);
#define K (1./8.)
for (int x = -1; x <= 1; x++)
for (int y = -1; y <= 1; y++)
if (x!=0||y!=0) {
    vec2 v = vec2(x,y);
    vec2 W = U+v;
    vec4 q = A(W);

    vec2 a = vel(Q.xy),
         b = vel(q.xy)+v;
    float ab = dot(v,b-a);
    if (inside(W)&&abs(ab)>0.) {
        float i = dot(v,(0.5*v-a))/ab;
        float j = .5+.25*clamp(1.-q.w,0.,1.);
        float k = .5+.25*clamp(1.-Q.w,0.,1.);
        float wa = K*Q.w*min(i,j)/j;
        float wb = K*q.w*max(k+i-1.,0.)/k;
        dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
    } else {
        dQ += K*Q.w*vec4(Q.xyz,1);
    }

}
if (dQ.w>0.) dQ.xyz /= dQ.w;
Q = dQ;
}

Main {

    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,0));
    vec4 w = A(U-vec2(0,0));
    Q.xy = 1.-A(U).ww;
    Q.z = e.w-w.w;
    Q.w = n.w-s.w;

}

Main {
    Q = A(U);
    vec3 h = hash(U+float(iFrame)*R);

    vec2 d = vec2(cos(h.x*2.*pi),sin(h.x*2.*pi));

    U -= d*h.y;
    if (iFrame > M) 
    for (float i = 0.; i < 400.; i++) {

        U += 2.*d;
        vec4 b = B(U);
        d = normalize(d+2.*(.5+.5*h.z)*b.zw);
        float s = (R.y-U.y<1.)?1.:0.;
        vec4 q = pow(max(d.y,0.),4.)*s*max(sin(6.*h.z-3.+vec4(1,2,3,4)),0.);
        Q += q/20.;

    }
    if (iFrame < 1) Q = vec4(0);
}
