float l_ntsc(vec3 t){return dot(t, vec3(.299,.587,.184));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord-.5*iResolution.xy;
    uv /= iResolution.y;
    float t = iTime*1.8;
    uv += vec2(cos(t*.8), sin(t)*.5)*.2;
    float l = length(uv);
    float f = 1.0 / l;

    vec2 st1 = vec2(f+iTime*2.0, atan(uv.y/uv.x)+iTime);
    vec2 st2 = vec2(f+iTime*3.5, atan(uv.y/uv.x)+iTime*1.5);

    vec4 tx1 = texture(iChannel0, st1);
    tx1 *= vec4(.35,.35,.6,1.) * 3.;
    vec4 tx2 = texture(iChannel1, st2 * vec2(.5, .5));
    tx2 = pow(tx2, vec4(.8))*1.8;
    tx2 = l_ntsc(tx2.xyz)*vec4(.3,.4,.8,1.);

    float c = .5+.5*cos(t*.8);
    vec4 col = mix(tx1, tx2, c*.5) * pow(l, .45);

    fragColor = col;
}
