// Signed distance function for an ellipse (oval)
float sdEllipse(vec2 p, vec2 r)
{
    // Calculate the normalized distance
    vec2 k = abs(p) / r;
    return (length(k) - 1.0) * min(r.x, r.y);
}

// Signed distance function for a square (box)
float sdSquare(vec2 p, float size)
{
    vec2 d = abs(p) - vec2(size / 2.0);
    return max(d.x, d.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    // Adjust for aspect ratio
    uv.x *= iResolution.x / iResolution.y;

    // Square parameters
    float squareSize = 0.5;               // Size of the square
    vec2 squareCenter = vec2(0.0, 0.0);  // Center of the square

    // Oval parameters
    vec2 ovalRadii = vec2(0.3, 0.15);      // Radii along x and y axes
    vec2 ovalCenter = vec2(0.0, 0.2);     // Center of the oval

    // Compute signed distances
    float dSquare = sdSquare(uv - squareCenter, squareSize);
    float dOval = sdEllipse(uv - ovalCenter, ovalRadii);

    // Combine the square and oval using union
    float d = min(dSquare, dOval);

    // Smooth edges for anti-aliasing
    float thickness = 0.005;
    float color = smoothstep(0.0, thickness, -d);

    // Set the fragment color (black shape on white background)
    fragColor = vec4(vec3(color), 1.0);
}
