vec3 palette(float t)
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.263,0.416,0.557);
    return a + b * cos(6.28318*(c*t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized UV coordinates
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Keep a copy of the original uv for reference
    vec2 uv0 = uv;

    // Increase amplitude and range of z for a stronger depth effect
    float z = sin(iTime * 1.0) * 1.0;

    vec3 pos = vec3(uv, z);

    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 4.0; i++)
    {
        // Apply perspective scaling: objects farther in z appear smaller
        pos.xy /= (1.0 + pos.z * 0.5);

        // Scale and wrap coordinates to create the repeating patterns
        pos.xy *= 1.5;
        pos.xy = fract(pos.xy);
        pos.xy -= 0.5;

        // Compute distance in 3D space
        float d = length(pos) * exp(-length(uv0));

        // Introduce z-based variation into color selection
        vec3 col = palette(length(uv0) + i * 0.4 + iTime * 0.4 + pos.z * 0.2);

        // Sinusoidal modulation of distance
        d = sin(d * 8.0 + iTime) / 8.0;
        d = abs(d);

        // Invert and scale to create a glow-like effect
        d = pow(0.01/d, 2.0);

        finalColor += col * d;

        // Increment z more noticeably each iteration to create layered depth
        z += 0.2;
        pos.z = z;
    }

    fragColor = vec4(finalColor, 1.0);
}
