// "Simple Cinematic Fire" by Ruud Helderman, November/December 2024 - MIT License
// https://www.shadertoy.com/view/lcGfWW
// Experimenting with frequency modulation to generate a 2D fire effect without much code.
// Combined with (simplistic) chroma keying to turn JCVD into a silhouette against a blazing background.

void mainImage(out vec4 O, vec2 U)
{
    vec2 R = iResolution.xy;

    vec4 s = vec4(2, 3, 4, 5),
         t = vec4(5, 45, 4, 25) * iTime,
         a = U.xyxy / R.y * s.wzzy,
         b = a - .2 * t,
         c = texture(iChannel0, U / R);

    O = mix(c.ggba - s * (.2 + .03 * sin(t.z) * sin(t.w)),
            vec4(2. * length(sin(b + sin(b.yxwz * s.xxyy + sin(b * s.wwzz)))) - a.w - cos(a.z) - .5 * s),
            smoothstep(.2, .5, c.g - c.b));
}
