const uint steps = 256u;

vec2 ccos(vec2 z)
{
    return vec2(cos(z.x) * cosh(z.y), -sin(z.x) * sinh(z.y));
}

vec3 trap(vec2 p)
{
    float a = 2. * min(abs(p.x), abs(p.y));
    float b = 1. * length(vec2(cos(p.x) + 0.75, p.y));
    float c = (cos(p.x) + 1.001) * p.y * p.y;
    return vec3(a, b, c);
}
void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = 8. * (2. * I - iResolution.xy) / iResolution.y;
    vec2 uvMouse = 8. * (2. * iMouse.xy - iResolution.xy) / iResolution.y;
    vec2 c = uv / (length(uvMouse) + 1.) + uvMouse;
    vec2 z = vec2(0.);
    vec3 dist = vec3(100.);
    for(uint i = 0u; i < steps; i++)
    {
        z = ccos(z + vec2(0.25 * iTime, 0.));
        z += c;
        dist = min(dist, trap(z));
    }
    vec3 col = exp(-2. * dist);
    O = vec4(col,1.0);
}
