void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv += cos(uv*iTime*.1)*.04;
    vec3 col = step(mod(uv.xyy + vec3(sin(iTime*.4)*.4,iTime*.07,iTime*.18), 1./5.), vec3(.03));
    col *= fract(sin(uv.x*uv.y*.003) * vec3(2093.29, 3313.32, 4335.32));
    fragColor = vec4(col,1.0);
}
