float time;
#define FAR 200.
#define ID_NONE -1.


#define ID_VASE 0.
#define ID_STEM 4.
#define ID_PESTIL 1.
#define ID_STAMEN 2.
#define ID_PETAL 3.
#define IF_LEAF 6.

//Пестик
Object BudPestil(vec3 p) {
	vec3 q;
	float d;

	Object object = Object(FAR, ID_PESTIL, p);
	{
		q = p;
		TF_TRANSLATE(q.y, -0.1);
		float r = 0.1;
		float f = TF_STEP_BETWEEN2(q.y, 0., 1.8)-0.05;
		float f1 = TF_SMOOTHSTEP_AFTER(q.y, 0., 0.2)*TF_SMOOTHSTEP_BEFORE(q.y, 0.8, 0.5);
		float f2 = TF_SMOOTHSTEP_AFTER(q.y, 1.6, 0.1);
		d = TF_BALL(q.xz, f * (r + 0.2*f1 + 0.05*f2));
		object.distance = d;
		object.position = q;
	}
	return object;
}
//Тычинка
Object BudStamen(vec3 p) {
	vec3 q;
	float d;

	Object object = Object(FAR, ID_STAMEN, p);
	{
		q = p;
		TF_ROTATE(q.yz, radians(-30.));
		float h = 2.;
		float an = radians(30.)*clamp( q.y/h, 0.0, 1.0 );
		TF_ROTATE(q.yz, an);
		float r = 0.02;
		float f = TF_STEP_BETWEEN2(q.y, 0., 2.)-0.05;
		float f1 = TF_SMOOTHSTEP_AFTER(q.y, 1.0, 0.1);
		d = TF_BALL(q.xz, f * (r + f1*0.03));
		object.distance = d;
		object.position = q;
	}
	return object;
}
//Лепестки
Object BudPetal(vec3 p) {
	vec3 q;
	float d;

	Object object = Object(FAR, ID_PETAL, p);
	{
		q = p;
		TF_ROTATE(q.yz, radians(30.));
		TF_TRANSLATE(q.y, -0.2);

		float anim = -150.-300.*sin(0.5*time);
		anim = clamp(anim, -110., 30.);

		float an = radians(anim)*clamp( pow(q.z/(2.5), 2.), 0.0, 1.0);
		TF_ROTATE(q.yz, -an);
		TF_TRANSLATE(q.z, 0.7);
		float r1 = 0.3, r2 = 0.6, s = 0.5;
		d = TF_BOX_ROUND(q.xz, vec2(0, s), mix(r1, r2, q.z/s));
		d = AND(d, TF_BETWEEN(q.y, 0.01));
		object.distance = d*0.25;
		object.position = q;
	}
	return object;
}

//Бутон
Object Bud(vec3 p) {
	vec3 q;
	float d;

	Object object = Object(FAR, ID_NONE, p);

	//Пестик
	object = OR(object, BudPestil(p));

	//Тычинки
	q = p;
	TF_REPLICA_ANGLE(q.xz, 3., 0.);
	TF_TRANSLATE(q.z, 0.18);
	object = OR(object, BudStamen(q));

	//Лепестки
	q = p;
	TF_REPLICA_ANGLE(q.xz, 5., 0.);
	object = OR(object, BudPetal(q));

	return object;
}

//Лист
Object Leaf(vec3 p, float ann, float h) {
	vec3 q;
	float d;

	Object object = Object(FAR, IF_LEAF, p);
	{
		q = p;
		float an = radians(20.)*clamp( q.y/h, 0.0, 1.0 );
		TF_ROTATE(q.xy, an + ann);
		float f = TF_STEP_BETWEEN2(q.y, 0., h);
		TF_MIRROR(q.z, 0.);
		TF_ROTATE(q.xz, -radians(30.));
		float s = 1.0 - 1.0 * clamp( q.y/h, 0.0, 1.0 );
		d = TF_BOX2D(q.xz, f*vec2(0.01, s));
		d = AND(d, TF_BETWEEN2(q.y, 0., h));
		object.distance = d*0.5;
		object.position = q;
	}
	return object;
}

//Стебель
Object Stem(vec3 p, float h) {
	vec3 q;
	float d;

	Object object = Object(FAR, ID_STEM, p);
	{
		d = TF_SEGMENT(p, vec3(0,0.0,0.0), vec3(0.0,h,0.0), 0.1);
		object.distance = d*0.5;
	}
	return object;
}

//Цветок
Object Flower(vec3 p, float ann) {
	vec3 q;
	float d;

	Object object = Object(FAR, ID_NONE, p);

	//Изгиб
	const float h = 10.;
	float an = radians(10.)*clamp( p.y/h, 0.0, 1.0 );
	TF_ROTATE(p.xy, -an + ann );

	//Стебкль
	q = p;
	object = OR(object, Stem(q, h));

	//Бутон
	q = p;
	TF_TRANSLATE(q.y, h);
	object = OR(object, Bud(q));

	//Верхние листья
	q = p;
	TF_TRANSLATE(q.y, 1.);
	TF_MIRROR(q.x, 0.);
	object = OR(object, Leaf(q, ann, h), 0.1);

	//Нижние лмстья
	q = p;
	TF_ROTATE(q.xz, 0.5*PI);
	TF_TRANSLATE(q.y, 0.5);
	TF_MIRROR(q.x, 0.);
	object = OR(object, Leaf(q, ann, h-2.), 0.1);

	return object;	
}

float map( vec3 p, inout Object object ) {
	vec3 q;
	float d;

	object = Object(FAR, ID_NONE, p);

	q = p;
	TF_MIRROR(q.xz, vec2(5, 4));
	object = OR(object, Flower(q, 0.2*sin(time)));

	return object.distance;
}
float map(vec3 p) {
	Object object;
	return map(p, object);
}

vec3 mapNormal (vec3 p, float eps) {
	vec2 e = vec2 (eps, -eps);
	vec4 v = vec4 (
		map (p + e.xxx), 
		map (p + e.xyy),
	 	map (p + e.yxy), 
		map (p + e.yyx)
	);
	return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * vec3 (v.y, v.z, v.w));
}

float rayMarch(inout Ray ray) {
	ray.distance = ray.near;
	float steps;
	for (float i = 0.; i < 200.; ++i) {
		ray.position = ray.origin + ray.direction * ray.distance;
		ray.object.distance = map(ray.position, ray.object);
		ray.hit = abs(ray.object.distance) < ray.epsilon;
		if (ray.hit) break;
		ray.distance += ray.object.distance*ray.swing;
		if (ray.distance>ray.far) break;
		steps = i + 1.;
		if (steps>ray.steps) break;
	}
	return steps;
}

float softShadow( Ray ray, float k ) {
    float shade = 1.0;
    ray.distance = ray.near;    
	float steps = 1.;
    for ( int i = 0; i < 50; i++ ) {
		ray.position = ray.origin + ray.direction * ray.distance;
        ray.object.distance = map(ray.position);
        shade = min( shade, smoothstep( 0.0, 1.0, k * ray.object.distance / ray.distance)); 
		ray.hit = ray.object.distance < ray.epsilon;
		if (ray.hit) break;
        ray.distance += min( ray.object.distance, ray.far / ray.steps * 2. ); 
        if (ray.distance > ray.far ) break; 
		steps++;
		if (steps>ray.steps) break;
    }
    return min( max( shade, 0.0 ) + 0.5, 1.0 ); 
}

vec3 SkyCol (vec3 rd, vec3 sunDir) {
	float sd = clamp (dot(sunDir, rd), 0., 1.);
	vec3 skyCol = mix(vec3(1), vec3 (0.5, 0.75, 1.), rd.y+0.2);
	vec3 col = skyCol;
	col += 0.5 * vec3 (1., 0.4, 0.2) * pow (sd, 256./2.);
	col += 0.5 * vec3 (1., 0.8, 0.7) * pow (sd, 1024./2.);
	return col;
}

vec3 lighting(Ray ray, vec3 lightDir, vec3 mColor) {
	float sh = 1.;
	#ifdef SOFT_SHADOW
		Ray ray1 = ray;
		{
			ray1.origin 	= ray.position;
			ray1.direction 	= lightDir;
			ray1.steps 		= 20.;
		}
		sh = softShadow(ray1, 16.);
	#endif
	float diff = max(dot(ray.normal,lightDir),0.0);
	float back = max(dot(ray.normal,-lightDir),0.0);
	float spec = max( 0.0, pow( clamp( dot(lightDir,reflect( ray.direction, ray.normal )), 0.0, 1.0), 16.0 ) );
	return mColor * (0.2 + 0.2*back + 0.8*diff*sh) + 0.7*spec*sh;
}

vec3 getMaterial(Ray ray) {
	vec3 p = ray.position;
	vec3 q = ray.object.position;
	vec3 n = ray.normal;
	vec3 mColor = vec3(1);

	if( ray.object.id==ID_PESTIL) {
		//Пестик
		float f = TF_SMOOTHSTEP_BEFORE(length(q.xz), 0.2, 0.2);
		mColor = mix(vec3(0.5,1.,0.0), vec3(1,0.1,0.1),  f );
	} else if(ray.object.id==ID_STAMEN) {
		//Тычинки
		mColor = vec3(1);
		if (q.y>1.) mColor = vec3(0.1);
	} else if( ray.object.id==ID_PETAL ) {
		//Лепестки
		mColor = vec3(0.8);
		q.z -= -0.7;
		q = vec3(atan( q.x, q.z ), q.y, length(q.xz));
		float rnd = noise( q.xz * vec2(32.0,0.8) ) * 0.5;			
		float f = TF_SMOOTHSTEP_AFTER(q.z + rnd, 1.6, 0.4);
		mColor = mix( mColor, vec3(1,0.0,0.0), f );
	} else if( ray.object.id==ID_STEM ) {
		//Стебель
		mColor = vec3(0.2,1,0.0);
	} else if( ray.object.id==IF_LEAF ) {
		//Лист
		mColor = vec3(0.2,1,0.0);
	}

	return mColor;
}

vec3 Render(Ray ray) {
	rayMarch(ray);

	vec3 col = vec3(0);
	vec3 lightDir = normalize(vec3(sin(u_time*0.5)*0.5,0.35,-0.7*cos(u_time*0.5)));

	if( ray.distance<ray.far ) {
		ray.normal = mapNormal(ray.position, ray.epsilon*10.);
		vec3 mColor = getMaterial(ray);
		col = lighting(ray, lightDir, mColor);
	} else {
		col = SkyCol(ray.direction, lightDir);
	}

	return col;
}

vec3 Path(float t) {
	return vec3(
		5.*sin(t),
		12. - 6.*cos(0.5*t),
		10.*cos(t)
	);
}

void main() {
	time = u_time;

	float aspect = u_canvas.x/u_canvas.y;
	vec2 uv = gl_FragCoord.xy / u_canvas.xy;
	vec2 uv1 = uv;
	uv = uv * 2.0 - 1.0;

	vec2 mouse = u_mouse.xy / u_canvas.xy - 0.5;
	if (u_mouse.xy==vec2(0)) mouse = vec2(0);

    vec2 ori = vec2(
        u_mouse.z==0. ? 0. : mouse.y*PI*2.,
        u_mouse.z==0. ? 0. : mouse.x*PI*2.
    );
	#if 1
		ori.x = clamp(ori.x, -PI/2., PI/2.);
	#else
		ori.x = clamp(ori.x, -0., PI/2.);
	#endif

	Camera cam;
	{
		cam.fov     = 60.;
		cam.aspect  = aspect;
		cam.origin  = Path(time*0.5);
		cam.target 	= vec3(0,12.,0);
		cam.up 		= vec3(0,1,0);
		cam.vMat 	= mat3(1);//TF_ROTATE_Y(ori.y) * TF_ROTATE_X(ori.x);
		cam.mMat	= mat3(1);//TF_ROTATE_Y(ori.y) * TF_ROTATE_X(ori.x);
	}

	Ray ray = lookAt(uv, cam);
	{
		ray.near 	= 0.01;
		ray.far		= FAR;
		ray.epsilon = 0.001;
		ray.swing	= 1.;//dot(ray.direction, vec3(0,0,1))<0.8 ? 0.5 : 1.;
		ray.steps	= 200.;
	}

	vec3 col = Render(ray);

	col = pow( clamp(col,0.0,1.0), vec3(0.45) );

	gl_FragColor = vec4( col, 1.0 );
}

const float PI = radians(180.);
//Камера
struct Camera {
	//Задаваемые параметры
	float fov, aspect;
	vec3  origin, target, up;
	mat3 vMat, mMat;
	//Расчетные параметры
	float factor;
	vec3  forward, right, position, coord;
	mat3 pMat, uMat;
};

//Параметры объекта
struct Object {
	float   distance;	//Последнее приближение к объекту сцены
	float 	id;			//id найденого объека сцены
	vec3 	position;	//Координатв поверхности объекта
};

//Луч
struct Ray {
	//Задаваемые параметры
	vec3  origin;		//Начало луча
	vec3  direction;	//Направление луча
	float near;			//Минимальное расстояние от камеры (начало сцены)
	float far;			//Максивальное расстояние от камеры (конец сцены)
	float epsilon;		//Точность обнаружения поверхности
	float steps;		//Максимальное число итераций
	float swing;		//"+" -колебание в найденной точке, "-"
	//Вычисляемые параметры
	float distance; 	//Расстояние до объекта сцены от ray.origin в направлении ray.direction
	vec3  position; 	//Точка поверхности
	vec3  normal;		//Нормаль в точке поверхности
	bool  hit;			//Флаг нахождения точки поверхности с заданной точностью
	Object object;		//Параметры объекта (можно формировать непосредственно в карте расстояний)
};
//Формирование луча камеры
Ray lookAt (in vec2 uv, inout Camera cam) {
	//Расчетные характеристики камеры
	cam.factor		= 1.0/tan(radians(cam.fov/2.));
	cam.forward 	= normalize(cam.target-cam.origin); 
	cam.right 		= normalize(cross(cam.up, cam.forward));
	cam.up 			= cross(cam.forward, cam.right);
	cam.position 	= cam.origin + cam.factor * cam.forward;
	cam.coord 		= cam.position + uv.x * cam.right*cam.aspect + uv.y * cam.up;
	cam.pMat 		= mat3(cam.right, cam.up, cam.forward);

	//Формирование луча
	Ray ray;
	{
		ray.origin 		= cam.mMat * cam.origin;
		ray.direction 	= cam.mMat * cam.vMat * cam.pMat * normalize(vec3(uv.x*cam.aspect, uv.y, cam.factor));
		//Умолчания
		ray.near		= 0.01;
		ray.far			= 100.;
		ray.epsilon		= 0.001;
		ray.swing		= 1.0;
		ray.steps		= 200.;
	}
	return ray;
}
//-------------------функции трансформации и позиционирования объектов сцены
//Допустимая ошибка (для удаления артефактов) 1./min(u_canvas.x, u_canvas.y)
float EPS = 0.01;
//Функция расстояния до фигуры со скругленными ребрами (объединение и сглаживание с увеличением на R)
//float d - сглаживание фигуры
//vec2 d - объединение и сглаживание двух фигур
//vec3 d - объединение и сглаживание трех фигур
//vec4 d - объединение и сглаживание четырех фигур
#define TF_ROUND(d, R) (length(max(d, 0.)) - (R))
//Функция расстояния до бокса (p vec3) или прямоугольника (p vec2) со скругленными ребрами
//Функция расстояния до капсулы, если два размера 0
#define TF_BOX_ROUND(p, S, R) TF_ROUND(abs(p)-(S), R)
#define TF_BOX_ROUND1(p, S, R) TF_ROUND(abs(p)-(S) + R, R)
//Функция расстояния до бокса (p vec3) или прямоугольника (p vec2)
#define TF_BOX(p, S) TF_BOX_ROUND(p, S, EPS)
//#define TF_BOX(p, S) TF_ROUND(abs(p) - (S) - EPS, EPS)
#define TF_BOX1(p, S) TF_BOX_ROUND(p, S, 0.)
#define TF_BOX3D(p, S) max(abs((p).x)-(S).x, max(abs((p).y)-(S).y, abs((p).z)-(S).z))
#define TF_BOX2D(p, S) max(abs((p).x)-(S).x, abs((p).y)-(S).y)
//Функция расстояния до шара (p vec3) или круга (p vec2)
#define TF_BALL(p, R) TF_ROUND(abs(p), R)
//Эллипс
#define TF_ELLIPSE3D(p, r) min ((r).x, min((r).y, (r).z)) * TF_BALL(p/(r), 1.)
#define TF_ELLIPSE2D(p, r) min ((r).x, (r).y) * TF_BALL(p/(r), 1.)
//Отрезок от a до b радиусом r
#define TF_SEGMENT(p, a, b, r) TF_BALL((p)-(a) - ((b)-(a)) * clamp( dot((p)-(a),(b)-(a))/dot((b)-(a),(b)-(a)), 0.0, 1.0 ), r)
//Функция расстояния до пространства ДО p1
#define TF_BEFORE(p, p1) (p - (p1))
#define TF_BEFORE1(p, p1) TF_ROUND(p - (p1) + EPS, EPS)
//Функция расстояния до пространства ДО normal с отступом p1
#define TF_BEFORE_PLANE(p, normal, p1) dot(p, normal) - p1
//Функция расстояния до пространства ПОСЛЕ p1
#define TF_AFTER(p, p1) (-p + (p1))
#define TF_AFTER1(p, p1) TF_ROUND(-p + (p1) + EPS, EPS)
//Функция расстояния до пространства ПОСЛЕ normal с отступом p1
#define TF_AFTER_PLANE(p, normal, p1) dot(p, normal) + p1
//Функция расстояния до симметричного пространства МЕЖДУ -p1 и +p1
//#define TF_BETWEEN(p, p1) TF_ROUND(abs(p) - (p1) + EPS, EPS)
#define TF_BETWEEN(p, p1) (abs(p) - (p1))
//Функция расстояния до несимметричного пространства МЕЖДУ p1 и p2
//#define TF_BETWEEN2(p, p1, p2) TF_ROUND( abs(p - 0.5*(p1+p2)) - 0.5*(p2-p1) + EPS, EPS)
#define TF_BETWEEN2(p, p1, p2) (abs(p - 0.5*((p1)+(p2))) - 0.5*((p2)-(p1)))
//Перемещение пространства p (float p...vec4 p)
#define TF_TRANSLATE(p, d) p -= d;
//Масштаб
#define TF_SCALE(p, s) p /= s

//Вращение пространства p (vec2 p)
#if 0
	#define TF_ROTATE(p, a) p = mat2(cos(a), sin(a), -sin(a), cos(a)) * p
#else
	#define TF_ROTATE(p, a) p = p.xy * cos (a) * vec2 (1., 1.) + p.yx * sin (a) * vec2 (-1., 1.)
#endif
#define TF_ROTATE_MAT2(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define TF_ROTATE_X(a) mat3(1.0,    0.0, 0.0,       0.0, cos(a), -sin(a),   0.0, sin(a), cos(a))
#define TF_ROTATE_Y(a) mat3(cos(a), 0.0, -sin(a),   0.0, 1.0, 0.0,   	    sin(a), 0.0, cos(a))
#define TF_ROTATE_Z(a) mat3(cos(a), -sin(a), 0.0,   sin(a), cos(a), 0.0,   	0.0, 0.0, 1.0)
//Отражение пространства p (float p...vec4 p)
#define TF_MIRROR(p, d) p = abs(p) - (d)
//Разбиение пространства на повторяющиеся. Возвращает id центра ячейки
//float p - пространство 1D
//vec2 p - пространство 2D
//vec3 p - пространство 3D
#define TF_REPLICA(p, d) \
	floor((p)/(d) + 0.5);\
	p = mod((p) + 0.5*(d), d) - 0.5*(d)
#define TF_REPLICA1(p, d) \
	floor((p)/(d) + 0.5);\
	p = p - (d) * floor((p)/(d) + 0.5)
//Разбиение пространства на повторяющиеся в диапазоне id от ida до idb включительно. Возвращает id центра ячейки
#define TF_REPLICA_LIMIT(p, d, ida, idb) \
	floor((p)/(d) + 0.5);\
	p = p-(d)*clamp(floor((p)/(d) + 0.5), ida, idb)

//Разбиение пространства на повторяющиеся сектора в плоскости vec2 p в полярной системе координат
//Возвращает id сектора	
#define TF_REPLICA_ANGLE_POLAR(p, n, off)\
	floor(mod(atan(p.x, p.y) + off, 2.*PI)/(2.*PI/(n)) + 0.5);\
	p = vec2(atan(p.x,p.y) + off, length(p.xy));\
	p.x = mod(p.x + 0.5*(2.*PI/(n)), (2.*PI/(n))) - 0.5*(2.*PI/(n));
//Разбиение пространства на повторяющиеся сектора в плоскости vec2 p в декартовой системе координат
//Возвращает id сектора	
#define TF_REPLICA_ANGLE(p, n, off) \
	TF_REPLICA_ANGLE_POLAR(p, n, off);\
	p = p.y * vec2(sin(p.x), cos(p.x));


//------------------------------------------------------------------------------------------------------
//Возвращает 0 или 1 в зависимоти от знячения p относительно a или b
//Операция ИЛИ(OR) 	: step1 + step2 (сложение)
//Операция И(AND) 	: step1 * step2 (умножение)
//Операция NOT 		: 1. - step
//------------------------------------------------------------------------------------------------------
//Возвращает 1 если p > a
//    1-----
//    |
//    |
//----0
//    a
#define TF_STEP_AFTER(p, a) step(a, p)

//Возвращает 1 если p < a
//----1
//    |
//    |
//    0-----
//    a
#define TF_STEP_BEFORE(p, a) step(p, a)

//Возвращает 1 если -a < p < a
//    1------1
//    |      |
//    |      |
//----0      0--------
//   -a      +a
#define TF_STEP_BETWEEN(p, a) TF_STEP_BEFORE(abs(p), a)
//Возвращает 1 если a < p < b
//    1------1
//    |      |
//    |      |
//----0      0--------
//    a      b
#define TF_STEP_BETWEEN2(p, a, b) TF_STEP_AFTER(p, a)*TF_STEP_BEFORE(p, b)

//------------------------------------------------------------------------------------------------------
//Возвращает 0 или 1 в зависимоти от знячения p относительно a или b со сглаживанием [-0.5*eps, 0.5*eps]
//Операция ИЛИ(OR) 	: smoothstep1 + smoothstep2 (сложение)
//Операция И(AND) 	: smoothstep1 * smoothstep2 (умножение)
//Операция NOT 		: 1. - smoothstep
//------------------------------------------------------------------------------------------------------
//Возвращает 
//	0 если p < a-eps/2; 
//	1 если p > a+eps/2;
//  сглаживание от 0 до 1  между a-0.5*eps и a+0.5*eps
//        1-----
//       /      
//      /       
//     /        
//----0         
//    |eps|
//      a 
#define TF_SMOOTHSTEP_AFTER(p, a, eps) smoothstep(a-0.5*eps, a+0.5*eps, p)

//Возвращает 
//	1 если p < a-eps/2; 
//	0 если p > a+eps/2;
//  сглаживание от 1 до 0  между a-eps/2 и a+eps/2
//-----1
//      \
//       \
//        \
//         0------
//     |eps|
//       a 
#define TF_SMOOTHSTEP_BEFORE(p, a, eps) (1.-smoothstep(a-0.5*eps, a+0.5*eps, p))

//Возвращает 
//	1 если p > -a+eps/2  и  p < a-eps/2; 
//	0 если p < -a-eps/2 или p > a+eps/2; 
//  сглаживание от 0 до 1  между -a-eps/2 и -a+eps/2
//  сглаживание от 1 до 0  между  a-eps/2 и  a+eps/2
//        1-----1
//       /       \
//      /         \
//     /           \
//----0             0------
//    |eps|     |eps|
//     -a         +a
#define TF_SMOOTHSTEP_BETWEEN(p, a, eps) TF_SMOOTHSTEP_BEFORE(abs(p), a, eps)

//Возвращает 
//	1 если p > a+eps/2  и  p < b-eps/2; 
//	0 если p < a-eps/2 или p > b+eps/2; 
//  сглаживание от 0 до 1  между a-eps/2 и a+eps/2
//  сглаживание от 1 до 0  между b-eps/2 и b+eps/2
//        1-----1
//       /       \
//      /         \
//     /           \
//----0             0------
//    |eps|     |eps|
//      a         b
#define TF_SMOOTHSTEP_BETWEEN2(p, a, b, eps) TF_SMOOTHSTEP_AFTER(p, a, eps)*TF_SMOOTHSTEP_BEFORE(p, b, eps)

//Возвращает для момента u_time номер отрезка времени 0,1,2...
//                -------
//               |
//        -------
//       |
//-------
//   0       1       2
#define TF_TIMER(u_time, intervalCount, intervalDuration) mod(floor(u_time/(intervalDuration)), (intervalCount))

//-------------------Функции сочетания расстояний до объектов сцены
//Сложение / Объединение / ИЛИ
float OR(float distA, float distB) {
	return min(distA, distB);
	//if (distB<distA) return distB; return distA;
	//return mix(distA, distB, step(distB, distA)); 
}
//Умножение / Пересечение / И
float AND(float distA, float distB) {
	return max(distA, distB);
	//if (distB>distA) return distB; return distA;
	//return mix(distA, distB, step(distA, distB)); 
}
// Минимальная функция сглаживания. Есть бесчисленное множество статей, но IQ объясняет это лучше всего здесь:
// http://iquilezles.org/www/articles/smin/smin.htm
//Гладкое сложение / Объединение / ИЛИ (k==0 без)
float OR( float distA, float distB, float k ) {
	float h = clamp( 0.5 + 0.5*(distB-distA)/k, 0., 1. );
	return mix( distB, distA, h ) - k*h*(1.-h);
}
//Гладкое умножение / Пересечение / И (k==0 без)
#if 0
