mat2 rotate2d(float c){
return mat2(cos(c), -sin(c), sin(c), cos(c));
}

float sdSphere(vec3 p, float s){
return length(p) - s;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q, 0.0)) + max(min(q.x,min(q.y,q.z)),0.0);
}

vec3 intersection_new_colour(vec3 p, vec3 centre, float r){
    float a = p.x;
    float b = p.y;
    float c = p.z;
    float x0 = centre.x;
    float y0 = centre.y;
    float z0 = centre.z;
    float alp = min((((a*x0 + b*y0 + c*z0) + sqrt(-(b*x0 - a*y0)*(b*x0 - a*y0) - (c*y0 - b*z0)*(b*z0 - c*y0) - (c*x0 - a*z0)*(c*x0 - a*z0) + r*r*length(p)*length(p)))/(length(p)*length(p))),
                    (((a*x0 + b*y0 + c*z0) - sqrt(-(b*x0 - a*y0)*(b*x0 - a*y0) - (c*y0 - b*z0)*(b*z0 - c*y0) - (c*x0 - a*z0)*(c*x0 - a*z0) + r*r*length(p)*length(p)))/(length(p)*length(p))));
    if(-alp*p.y > .0){
        return vec3(100.0,50.,10.);}
    else{
        return vec3(10.,50.0,100.);}
}

vec3 intersection_n_colour(vec3 p, vec3 centre, float r){
    float a = p.x;
    float b = p.y;
    float c = p.z;
    float x0 = centre.x;
    float y0 = centre.y;
    float z0 = centre.z;
    float alp = min((((a*x0 + b*y0 + c*z0) + sqrt(-(b*x0 - a*y0)*(b*x0 - a*y0) - (c*y0 - b*z0)*(b*z0 - c*y0) - (c*x0 - a*z0)*(c*x0 - a*z0) + r*r*length(p)*length(p)))/(length(p)*length(p))),
                    (((a*x0 + b*y0 + c*z0) - sqrt(-(b*x0 - a*y0)*(b*x0 - a*y0) - (c*y0 - b*z0)*(b*z0 - c*y0) - (c*x0 - a*z0)*(c*x0 - a*z0) + r*r*length(p)*length(p)))/(length(p)*length(p))));
    if(-alp*p.y > .0){
        return vec3(10.0,50.,100.);}
    else{
        return vec3(100.,50.0,10.);}
}

vec4 map(vec3 p){
vec3 s1position = vec3(-1.,0.,-0.5);
vec3 s2position = vec3(1.,0.,0.5);
vec3 s3position = vec3(3, 0.,.0);
vec3 s4position = vec3(-3, 0., .0);
vec3 q = p;
q.xz *= rotate2d(iTime);
vec4 sphere1 = vec4(intersection_new_colour(q, s1position, 0.7), sdSphere(q - (s1position),.7));
vec4 sphere2 = vec4(intersection_n_colour(q,s2position,0.7), sdSphere(q - (s2position),.7));
vec4 box1 = vec4(0.,0.,0., sdBox(q , vec3(.75)));
s3position.xy *= rotate2d(iTime);
s4position.xy *= rotate2d(iTime);
vec4 box2 = vec4(100.,50.,10.,sdBox(p - s3position, vec3(.5)));
vec4 box3 = vec4(10.,50.,100., sdBox(p - s4position, vec3(.75)));
vec4 ground = vec4(100.,100.,100., p.y + 0.75);
vec4 res = vec4(100.,100.,100.,100.);
float dist = min(ground.w, min(min(min(box1.w, box2.w), box3.w), min(sphere1.w,sphere2.w)));
if(ground.w == dist){
        res = ground;
    } else if(box1.w == dist){
        res = box1;
    } else if(box2.w == dist){
        res = box2;
    } else if(box3.w == dist){
        res = box3;
    } else if(sphere1.w == dist){
        res = sphere1;
    } else if(sphere2.w == dist){
        res = sphere2;
    }
return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;
    vec2 m = (iMouse.xy *2. - iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0.,0.,-3.);
    vec3 rd = normalize(vec3(uv,1.));
    ro.xz *= rotate2d(-m.x);
    rd.xz *= rotate2d(-m.x);
    vec3 col = vec3(0., 0., 0.);
    float t = 0.; 
    int i = 0;
    vec4 d;
    while(i < 80 && (d.w < .001 || t > .4)){
        vec3 p = ro + rd*t;
        d = map(p);
        col = vec3(d.rgb)/80.;
        t += d.w;
        i += 1;
    }

    fragColor = vec4(col,1.);
}
