#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);

    for(j=0.;j++<16.;)
        p.xz=abs(abs(p.xy*R2D(i+iTime/3.)))-.04,
        d=max(max(p.z/d,p.y/e),p.z*cos(i)),
        d-=dot(cos(p),sin(p))/2e4;

    e+=d;
}o+=vec4(sqrt(p/d),1)/i;
}
