void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= 1.0;


    vec4 col = vec4(0.);



    col = texture(iChannel0, uv);



     mat2 rot = mat2(vec2(col.x, col.y),
            vec2(-col.z, col.w));

    mat2 identity = mat2(vec2(1.0, 0.0), vec2(0.0, 1.0));

    float a = (1.0 + sin(iTime)) / 2.0;

    mat2 skew = (identity * (1.0 - a)) + (rot * a);
    uv *= skew;
    uv *= skew;

    col = texture(iChannel0, uv);


    fragColor = col;
}
