// Feel free to do whatever you want with this :)
// https://twitter.com/TheMirzaBeig/status/1734221833061228801

// Help shorten code.

#define v3 vec3
#define R iResolution.xy

// Hash (3D).

vec3 H(v3 p)
{
    return fract(sin(v3(

    dot(p,v3(1.,57.,113.)),
    dot(p,v3(57.,113.,1.)),
    dot(p,v3(113.,1.,57.))))*43.);
}

// Distance (3D Minkowski).

float D(v3 x,v3 y,float p) 
{
    return clamp(pow(
    pow(abs(x.x-y.x),p)+
    pow(abs(x.y-y.y),p)+
    pow(abs(x.z-y.z),p),1./p),0.,1.);
}

// Noise (3D Voronoi).

float N(vec3 p) 
{
    float d=1e9,m;v3 i,v;
    for(i.z=-1.;i.z<=1.;i.z++)
    {for(i.y=-1.;i.y<=1.;i.y++)
        {for(i.x=-1.;i.x<=1.;i.x++) 
            {           
                v=i-fract(p)+H(floor(p)+i);                
                m=D(v,v3(0.),.4);d=min(d,m);
            }
        }
    } return d;
}

// Pixelate function
vec2 pixelate(vec2 coord, float pixelSize) {
    return floor(coord / pixelSize) * pixelSize;
}

// Render.

void mainImage(out vec4 c,vec2 u) 
{
    float t=iTime*.04,n;
    u*=vec2(R.x/R.y,1.)/R.xy;
     // Apply pixelate effect
    float pixelSize = .004; // Change this to your desired pixel size
    u = pixelate(u, pixelSize);
    v3 u3=v3(u.x+t,u.y,t)*12.;    
    n=1.-N(u3);c=vec4(n);
}
