void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,s; vec3 p,q=vec3(2,4,-2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1);
    p.z-=4.;s=mod(iTime*.2,cos(-1.));
    p=vec3(log(cos(p.x))-s,p.y/s,atan(p.y,p.z));

    for(j=0.;j++<5.;)
        p=abs(abs(p)*tan(q)),
        d=dot(p,p)/2e4,
        e+=d-iTime*.018;

}o+=vec4(tan(-p*q),1)*3./i;
}
